import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { environment } from 'src/environments/environment';

@Component({
  selector: 'app-pmf',
  templateUrl: './pmf.component.html',
  styleUrls: ['./pmf.component.css']
})
export class PmfComponent implements OnInit {

  levelData: any;
  levelDetails: any[]=[];
  currentPage = 1;
  itemsPerPage = 10; // Adjust as needed
  loading = false;
  endOfData = false;



  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.levelData = history.state.levelData;
    if (this.levelData) {
      this.fetchLevelDetails(this.levelData.level);
    }
  }

  fetchLevelDetails(level: number): void {
    if (this.loading || this.endOfData) {
      return;
    }
    this.loading = true;
    const payerId = localStorage.getItem('_id');
    if (!payerId) {
      console.error('PayerId not found in localStorage');
      return;
    }

    const headers = new HttpHeaders({
      'Authorization': `Bearer ${localStorage.getItem('token')}`
    });

    const params = {
      page: this.currentPage.toString(),
      limit: this.itemsPerPage.toString()
    };

    this.http.get<any[]>(`${environment.apiUrl}/getTransactionsPMF/sender/${payerId}`, { headers, params })
      .subscribe(
        response => {
         console.log(response);

          this.loading = false;
        },
        error => {
          console.error('Error fetching matrix summary:', error);
          this.loading = false;
        }
      );
  }

}
