import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';

@Component({
  selector: 'app-take-help',
  templateUrl: './take-help.component.html',
  styleUrls: ['./take-help.component.css']
})
export class TakeHelpComponent implements {
  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.fetchTransactions();
  }

  fetchTransactions(): void {
    const payerId = localStorage.getItem('_id');
    if (!payerId) {
      console.error('PayerId not found in localStorage');
      return;
    }

    const headers = new HttpHeaders({
      'Authorization': `Bearer ${localStorage.getItem('token')}`
    });


    this.http.get<any[]>(`${environment.apiUrl}/payments/transactions/payer/${payerId}`, { headers })
      .subscribe(
        response => {
          this.transactions = response;
        },
        error => {
          console.error('Error fetching transactions:', error);
        }
      );
  }
}
