import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { environment } from 'src/environments/environment';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  formData = {
    email: '',
    password: ''
  };

  constructor(private http: HttpClient,private router: Router) {}

  loginUser(): void {
    this.http.post<any>(`${environment.apiUrl}/auth/login`, this.formData)
      .subscribe(
        response => {
          localStorage.setItem('token', response.token);
          localStorage.setItem('email', response.email);
          localStorage.setItem('username', response.username);
          localStorage.setItem('_id', response._id);
          this.router.navigate(['/dashboard']);
        },
        error => {
          console.error('Login error:', error);
        }
      );
  }

}
<div class="container mt-5">
  <div class="card shadow">
    <div class="card-header bg-primary text-white">
      <h2 class="text-center">Login</h2>
    </div>
    <div class="card-body">
      <form (ngSubmit)="loginUser()" #loginForm="ngForm">
        <div class="form-group">
          <label for="email">Email address</label>
          <input type="email" class="form-control" id="email" name="email" [(ngModel)]="formData.email" required>
        </div>
        <div class="form-group">
          <label for="password">Password</label>
          <input type="password" class="form-control" id="password" name="password" [(ngModel)]="formData.password" required>
        </div>
        <button type="submit" class="btn btn-primary btn-block" [disabled]="loginForm.invalid">Login</button>
      </form>
    </div>
    <div class="card-footer text-muted text-center">
      <p>Don't have an account? <a href="/register">Register here</a></p>
    </div>
  </div>
</div>
